package com.navy.practice;

import java.util.HashMap;
import java.util.Map;

public class MinWindow {
	
	public static void main(String[] args) {
		
		String s = "";
		String t = "";
		String result = new MinWindow().minWindow(s, t);
		System.out.println(result);
	}
	
	 public String minWindow(String s, String t) {
		 
		 String result = "";
		 
		 if(t.length() > s.length()) {
			 return result;
		 }
		 
		 boolean flag = false;
		 
		 int step = t.length();
		 
		 while(step <= s.length() && !flag) {
			 
			 for(int i = 0; i < s.length()-step+1; i++) {
				 
				 String target = s.substring(i,i+step);
				 flag = isContainAll(target, t);
				 
				 if(flag) {
					 result = target;
					 break;
				 }
			 }
			 
			 step++;
		 }
		 
		 return result;
	 }
	 
	 /**
	  * 字符串target是否包含字符串t的所有字母
	  * @param target
	  * @param t
	  * @return
	  */
	 private boolean isContainAll(String target, String t) {
		 
		 Map<String, Integer> map = new HashMap<String, Integer>();
		 for(int i = 0; i < target.length(); i++) {
			 String s = target.substring(i, i+1);
			 Integer sCount = map.get(s);
			 if(sCount == null) {
				 map.put(s, 1);
			 }
			 else {
				 map.put(s, ++sCount);
			 }
		 }
		 
		 for(int i = 0; i < t.length(); i++) {
			 String s = t.substring(i, i+1);
			 Integer sCount = map.get(s);
			 if(sCount == null || sCount == 0) {
				 return false;
			 }
			 else {
				 map.put(s, --sCount);
			 }
		 }
		 return true;
	 }
}
